import React, { useState } from 'react';
import questions from './questions.json';
import Question from './Question';
import './css/QuestionStyles.css'

function Quiz() {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [scores, setScores] = useState({
    E: 0,
    I: 0,
    S: 0,
    N: 0,
    T: 0,
    F: 0,
    J: 0,
    P: 0
  });
  const [selectedAnswer, setSelectedAnswer] = useState("");

  const handleAnswerOptionClick = (dimension, option) => {
    // Update scores for the chosen dimension (E/I, S/N, T/F, J/P)
    setScores(prevScores => ({
      ...prevScores,
      [dimension]: prevScores[dimension] + (option === 'A' ? 1 : 0)
    }));

    const nextQuestion = currentQuestion + 1;
    if (nextQuestion < questions.length) {
      setCurrentQuestion(nextQuestion);
      setSelectedAnswer(""); // Reset selected answer for the next question
    } else {
      // Calculate the user's MBTI type based on scores
      const mbtiType = `${scores.E >= scores.I ? 'E' : 'I'}${scores.S >= scores.N ? 'S' : 'N'}${scores.T >= scores.F ? 'T' : 'F'}${scores.J >= scores.P ? 'J' : 'P'}`;
      alert(`You have finished the quiz! Your MBTI type is: ${scores.N}`);
    }
  };

  return (
    <div className="app">
      {questions.length > 0 && (
        <Question
          data={questions[currentQuestion]}
          handleAnswerOptionClick={handleAnswerOptionClick}
          selectedAnswer={selectedAnswer}
          setSelectedAnswer={setSelectedAnswer}
        />
      )}
    </div>
  );
}

export default Quiz;
