import React from 'react';
import './css/QuestionStyles.css'; // Adjusted path for QuestionStyles.css

function Question({ data, isCurrentQuestion, handleAnswerOptionClick, selectedAnswer, setSelectedAnswer }) {
  return (
    <div className={`question ${isCurrentQuestion ? 'current' : 'inactive'}`}>
      <h1>{data.question}</h1>
      <div>
        {data.answers.map((answer, index) => (
          <label key={index} className={`answer ${selectedAnswer === answer.text ? 'selected' : ''}`}>
            <input
              type="radio"
              name={`answer-${data.id}`}
              value={answer.text}
              checked={selectedAnswer === answer.text}
              onChange={() => {
                setSelectedAnswer(answer.text);
                handleAnswerOptionClick(data.dimension, answer.option);
              }}
            />
            {answer.text}
          </label>
        ))}
      </div>
    </div>
  );
}

export default Question;
