import React from 'react';
import './css/QuestionStyles.css'; // Adjust path based on where your CSS file is located

function Question({ data, isLastQuestion, handleAnswerOptionClick, selectedAnswer, setSelectedAnswer }) {
  return (
    <div className="quiz-container">
      <h1>{data.question}</h1>
      <div>
        {data.answers.map((answer, index) => (
          <div key={index} style={{ marginBottom: '10px' }}>
            <input
              type="radio"
              id={`option-${index}`}
              name="answer"
              value={answer.text}
              checked={selectedAnswer === answer.text}
              onChange={() => setSelectedAnswer(answer.text)}
            />
            <label htmlFor={`option-${index}`}>
              {answer.text}
            </label>
          </div>
        ))}
      </div>
      {isLastQuestion ? (
        <button
          onClick={() => handleAnswerOptionClick(data.dimension, selectedAnswer === data.answers[0].text ? 'A' : 'B')}
          disabled={!selectedAnswer}
        >
          Calculate Score
        </button>
      ) : (
        <button
          onClick={() => {
            const selectedOption = data.answers.find(answer => answer.text === selectedAnswer)?.option;
            if (selectedOption) {
              handleAnswerOptionClick(data.dimension, selectedOption);
            }
          }}
          disabled={!selectedAnswer}
        >
          Next
        </button>
      )}
    </div>
  );
}

export default Question;
